PROFE_PW=#{PROFE_PW}, PROFE_BIRTH=#{PROFE_BIRTH}, PROFE_PHONE=#{PROFE_PHONE},PROFE_EMAIL=#{PROFE_EMAIL}, 
PROFE_POST=#{PROFE_POST}, PROFE_ADDR=#{PROFE_ADDR}, PROFE_EXTRA_ADDR=#{PROFE_EXTRA_ADDR}  
WHERE PROFE_NO=#{PROFE_NO};
